export class CheckoutComponent {
  //網頁載入時建構仔會先跑 然後跑ngOnInit (inject the service)
  constructor(formBuilder, luv2ShopFormService, cartService) {
    this.formBuilder = formBuilder;
    this.luv2ShopFormService = luv2ShopFormService;
    this.cartService = cartService; //這邊宣告的property就可以直接在HTML裡用{{ }}取得

    this.totalPrice = new BehaviorSubject(0); //0是起始資料 initial value

    this.totalQuantity = new BehaviorSubject(0);
    this.creditCardYears = [];
    this.creditCardMonths = [];
    this.countries = [];
